import React from 'react'
import { useParams } from 'react-router-dom'
import { getSingleMember } from '../../lib/api'
import Nav from '../common/Nav'

import miloHeadshot from '../images/milo-headshot.JPG'
import jamieHeadshot from '../images/jamie-headshot.jpg'

function MemberShow() {
  const [member, setMember] = React.useState(null)
  const [hasError, setHasError] = React.useState(false)
  const { id } = useParams()

  React.useEffect(() => {
    const getData = async () => {
      try {
        const { data } = await getSingleMember(id)
        setMember(data)
      } catch (err) {
        setHasError(true)
      }
    }
    getData()
  }, [id])

  return (
    <section className="section">
      <Nav />
      <h2 className='members-header'>Members</h2>
      <div className="member-container">
        {member.name[2] ?
          <div className="member-box">
              <div className='member-info-box' key={member.id}>
                <h2>{member.name}</h2>
                <img src={miloHeadshot} alt='profile' />
                {/* <img src={miloHeadshot} alt='profile' /> */}
                {/* <img alt='profile' src='https://i.stack.imgur.com/l60Hf.png' /> */}
                <p>HANDICAP: {member.handicap}</p>
                <p>HOME COURSE: {member.homeCourse}</p>
                <p>FAVOURITE COURSE PLAYED: {member.favCoursePlayed}</p>
                <p>BEST GOLF MEMORY: {member.bestGolfMemory}</p>
                <p>LOWEST ROUND: {member.lowestRound}</p>
                <p>FAVOURITE SHOT: {member.favShot}</p>
                <p>WEAKNESS: {member.weakness}</p>
                <p>BEST FINISH: {member.bestFinish}</p>
                <p>FAVOURITE LA CRUCHE MOMENT: {member.favLCMemory}</p>
              </div>
          </div>
          :
          <h2 className="error">
            {hasError ? 'Fore!' : ''}
          </h2>
        }
      </div>
    </section>
  )
}

export default MemberShow
